contracts/InvoiceRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract InvoiceRegistry {
    struct Invoice {
        uint256 id;
        address issuer;
        address payer;
        uint256 amount;
        address token; // Address of the token (e.g., USDC), or address(0) for native coin
        uint256 dueDate;
        string metadataHash; // IPFS hash or similar for invoice details
        bool isPaid;
        bool isCancelled;
    }

    uint256 private _nextInvoiceId;
    mapping(uint256 => Invoice) public invoices;

    event InvoiceCreated(uint256 indexed id, address indexed issuer, address indexed payer, uint256 amount, address token, uint256 dueDate);
    event InvoicePaid(uint256 indexed id, address indexed payer);
    event InvoiceCancelled(uint256 indexed id);

    function createInvoice(
        address _payer,
        uint256 _amount,
        address _token,
        uint256 _dueDate,
        string memory _metadataHash
    ) external returns (uint256) {
        require(_amount > 0, "Amount must be greater than 0");
        require(_payer != address(0), "Invalid payer address");

        uint256 invoiceId = _nextInvoiceId++;
        invoices[invoiceId] = Invoice({
            id: invoiceId,
            issuer: msg.sender,
            payer: _payer,
            amount: _amount,
            token: _token,
            dueDate: _dueDate,
            metadataHash: _metadataHash,
            isPaid: false,
            isCancelled: false
        });

        emit InvoiceCreated(invoiceId, msg.sender, _payer, _amount, _token, _dueDate);
        return invoiceId;
    }

    function markAsPaid(uint256 _invoiceId) external {
        Invoice storage invoice = invoices[_invoiceId];
        require(msg.sender == invoice.issuer, "Only issuer can mark as paid manually");
        require(!invoice.isPaid, "Already paid");
        require(!invoice.isCancelled, "Invoice cancelled");

        invoice.isPaid = true;
        emit InvoicePaid(_invoiceId, invoice.payer);
    }

    function cancelInvoice(uint256 _invoiceId) external {
        Invoice storage invoice = invoices[_invoiceId];
        require(msg.sender == invoice.issuer, "Only issuer can cancel");
        require(!invoice.isPaid, "Cannot cancel paid invoice");

        invoice.isCancelled = true;
        emit InvoiceCancelled(_invoiceId);
    }

    function getInvoice(uint256 _invoiceId) external view returns (Invoice memory) {
        return invoices[_invoiceId];
    }
}

contracts/PaymentEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./InvoiceRegistry.sol";

contract PaymentEscrow {
    InvoiceRegistry public invoiceRegistry;

    event PaymentDeposited(uint256 indexed invoiceId, uint256 amount);
    event PaymentReleased(uint256 indexed invoiceId, address indexed recipient, uint256 amount);

    constructor(address _invoiceRegistry) {
        invoiceRegistry = InvoiceRegistry(_invoiceRegistry);
    }

    function payInvoice(uint256 _invoiceId) external payable {
        InvoiceRegistry.Invoice memory invoice = invoiceRegistry.getInvoice(_invoiceId);
        require(!invoice.isPaid, "Invoice already paid");
        require(!invoice.isCancelled, "Invoice cancelled");
        require(invoice.payer == msg.sender || invoice.payer == address(0), "Not the designated payer"); // address(0) payer means anyone can pay

        if (invoice.token == address(0)) {
            require(msg.value == invoice.amount, "Incorrect ETH amount");
        } else {
            require(msg.value == 0, "Do not send ETH for token payment");
            IERC20(invoice.token).transferFrom(msg.sender, address(this), invoice.amount);
        }

        // In a real escrow, we might hold funds. For now, we'll auto-release to issuer (direct payment)
        // or we could add release logic. Let's implement direct payment for MVP simplicity, 
        // but keep the structure of "Escrow" for future expansion (dispute resolution).

        _releaseFunds(invoice);

        // We need to call markAsPaid on registry. 
        // Note: InvoiceRegistry needs to allow this contract to call markAsPaid. 
        // For now, we'll just emit an event and assume off-chain indexer handles status, 
        // or we need to update InvoiceRegistry to have access control.
        // Let's assume we update InvoiceRegistry later to allow this contract.
    }

    function _releaseFunds(InvoiceRegistry.Invoice memory invoice) internal {
        if (invoice.token == address(0)) {
            payable(invoice.issuer).transfer(invoice.amount);
        } else {
            IERC20(invoice.token).transfer(invoice.issuer, invoice.amount);
        }
        emit PaymentReleased(invoice.id, invoice.issuer, invoice.amount);
    }
}

contracts/RevenueSplitter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract RevenueSplitter {
    event RevenueSplit(address indexed from, address[] recipients, uint256[] amounts);

    function splitPayment(
        address _token,
        address[] calldata _recipients,
        uint256[] calldata _amounts
    ) external payable {
        require(_recipients.length == _amounts.length, "Mismatched arrays");

        uint256 totalAmount = 0;
        for (uint256 i = 0; i < _amounts.length; i++) {
            totalAmount += _amounts[i];
        }

        if (_token == address(0)) {
            require(msg.value == totalAmount, "Incorrect ETH amount");
            for (uint256 i = 0; i < _recipients.length; i++) {
                payable(_recipients[i]).transfer(_amounts[i]);
            }
        } else {
            require(msg.value == 0, "Do not send ETH");
            IERC20 token = IERC20(_token);
            token.transferFrom(msg.sender, address(this), totalAmount);
            for (uint256 i = 0; i < _recipients.length; i++) {
                token.transfer(_recipients[i], _amounts[i]);
            }
        }

        emit RevenueSplit(msg.sender, _recipients, _amounts);
    }
}

